import { checkHealth, sendMessage } from "../api/chatApi";

// Check health hook
const fetchServerHealthStatus = async () => {
  try {
    const healthStatus = await checkHealth();
    console.log('Server health:', healthStatus);
    // Handle the health status (e.g., update UI)
  } catch (error) {
    console.error('Health check failed:', error);
    // Handle the error (e.g., show error message to user)
  }
};

// Send message hook
const handleUserMessage = async (userMessage) => {
  try {
    const response = await sendMessage(userMessage);
    console.log('Server response:', response);
    // Handle the response (e.g., display in chat window)
  } catch (error) {
    console.error('Message request failed:', error);
    // Handle the error (e.g., show error message to user)
  }
};
