function MessageBox({ text = "No message provided", user = true }) {
  return (
    <div
      className={`p-4  bg-opacity-15 backdrop-blur-lg 
                backdrop-saturate-[185%] shadow-md ${
                  user
                    ? "ml-2 bg-user-chat text-right"
                    : "mr-2 bg-computer-chat text-left"
                }`}
    >
      <p>{text}</p>
    </div>
  );
}

export default MessageBox;
