import React, { useState, useRef, useEffect } from "react";
import MessageBox from "./MessageBox";
import computerAvatar from "../assets/computer-avatar.png";
import useChat from "../hooks/useChat";
import sendIcon from "../assets/send-icon.png";
import reloadicon from "../assets/reload-icon.png";

function ChatWindow() {
  const {
    messages,
    isLoading,
    error,
    serverHealth,
    handleUserMessage,
    fetchServerHealthStatus,
  } = useChat();
  const [inputMessage, setInputMessage] = useState("");
  const messagesEndRef = useRef(null);
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputMessage.trim()) {
      handleUserMessage(inputMessage);
      setInputMessage("");
    }
  };

  return (
    <div className="w-full max-w-screen-lg h-[80vh] shadow-md rounded-lg mx-auto  bg-gray-900 custom-blur-bg-40 border-solid border border-gray-400 flex flex-col">
      {/* Header div*/}
      <div className="h-10 bg-custom-light-purple pl-4 pr-4 flex items-center justify-between custom-blur-bg-40">
        <div className="flex items-center">
          <img src={computerAvatar} className="w-7" alt="Computer Icon" />
          <span className="pr-2" />
          <p>Computer</p>
        </div>
        <div>
          <button
            onClick={fetchServerHealthStatus}
            className={`mr-2 px-2 py-1 border rounded flex items-center text-xs`}
          >
            <img src={reloadicon} className="w-4 mr-2" alt="Reload" />
            System Health:
            <span
              className={`ml-1 font-bold ${
                serverHealth ? "text-green-500" : "text-red-500"
              }`}
            >
              {serverHealth ? "OK" : "Down"}
            </span>
          </button>
        </div>
      </div>

      {/* Messages div */}
      <div className="flex-grow w-full flex flex-col space-y-4 p-4 overflow-y-auto">
        {messages.map((message, index) => (
          <MessageBox key={index} text={message.text} isUser={message.isUser} />
        ))}
      </div>

      {/* Error Message */}
      {error && (
        <div className="bg-red-500 text-white p-2 text-center">{error}</div>
      )}

      {/* Input div */}
      <div className="w-full h-12 flex items-center justify-center">
        <form
          onSubmit={handleSubmit}
          className="flex items-center w-full max-w-screen-lg px-4"
        >
          <input
            className="p-1 flex-grow rounded-md text-black"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Type your message... (500 characters max)"
            disabled={isLoading}
            maxLength={500}
          />
          <span className="px-1" />
          <button
            type="submit"
            className="p-1 pr-0"
            disabled={isLoading || inputMessage.trim().length === 0}
          >
            <img src={sendIcon} className="w-8" alt="Send" />
          </button>
        </form>
      </div>
    </div>
  );
}

export default ChatWindow;
